'use strict';

const Package = require('./package');
const Project = require('./project');

module.exports = {
  Project,

  createProject(dir = process.cwd(), packsDir) {
    let project = new Project(dir, packsDir);
    return project;
  },

  createPackage(dir) {
    let pack = new Package(dir);
    return pack;
  },

  sendPackage(dir) {
    let pack = this.createPackage(dir);
    return pack.send(false);
  },
};
