'use strict';

const childProcess = require('child_process');

class Util {
  exec(command = 'ls', dir = process.cwd()) {
    return new Promise((resolve, reject) => {
      childProcess.exec(
        command,
        {
          cwd: dir,
        },
        (error, stdout, stderr) => {
          if (error) {
            reject(`${error}`);
            return;
          }
          resolve(`${stdout}`);
        }
      );
    });
  }
}

module.exports = new Util();
